INSERT OR IGNORE INTO alice_glossary (id, book_id, term, definition, example, chapter_reference)
VALUES ('glossary-390', 'alice-in-wonderland', 'grow up', 'become an adult', 'And when I grow up, I''ll write one—but I''m grown up now,'' she added in a sorrowful tone; ''at least there''s no room to grow up any more HERE.''', NULL);
INSERT OR IGNORE INTO alice_glossary (id, book_id, term, definition, example, chapter_reference)
