INSERT OR IGNORE INTO alice_glossary (id, book_id, term, definition, example, chapter_reference)
VALUES ('glossary-536', 'alice-in-wonderland', 'simpleton', 'a person lacking intelligence or common sense', '''Well, then,'' the Gryphon went on, ''if you don''t know what to uglify is, you ARE a simpleton.''', NULL);
INSERT OR IGNORE INTO alice_glossary (id, book_id, term, definition, example, chapter_reference)
VALUES ('glossary-537', 'alice-in-wonderland', 'giddy', 'lacking seriousness; given to frivolity', '''I said pig,'' replied Alice; ''and I wish you wouldn''t keep appearing and vanishing so suddenly: you make one quite giddy.''', NULL);
