VALUES ('glossary-538', 'alice-in-wonderland', 'sit up', 'change to an upright sitting position', 'And it''ll fetch things when you throw them, and it''ll sit up and beg for its dinner, and all sorts of things—I can''t remember half of them—and it belongs to a farmer, you know, and he says it''s so useful, it''s worth a hundred pounds!', NULL);
INSERT OR IGNORE INTO alice_glossary (id, book_id, term, definition, example, chapter_reference)
VALUES ('glossary-539', 'alice-in-wonderland', 'quite a', 'of an unusually noticeable or exceptional or remarkable kind', 'For the Mouse was swimming away from her as hard as it could go, and making quite a commotion in the pool as it went.', NULL);
