INSERT OR IGNORE INTO alice_glossary (id, book_id, term, definition, example, chapter_reference)
VALUES ('glossary-546', 'alice-in-wonderland', 'do by', 'interact in a certain way', '''Somebody said,'' Alice whispered, ''that it''s done by everybody minding their own business!''', NULL);
INSERT OR IGNORE INTO alice_glossary (id, book_id, term, definition, example, chapter_reference)
VALUES ('glossary-547', 'alice-in-wonderland', 'ear', 'the sense organ for hearing and equilibrium', 'There was not a moment to be lost: away went Alice like the wind, and was just in time to hear it say, as it turned a corner, ''Oh my ears and whiskers, how late it''s getting!''', NULL);
INSERT OR IGNORE INTO alice_glossary (id, book_id, term, definition, example, chapter_reference)
