INSERT OR IGNORE INTO alice_glossary (id, book_id, term, definition, example, chapter_reference)
VALUES ('glossary-559', 'alice-in-wonderland', 'rub', 'move over something with pressure', '''They were learning to draw,'' the Dormouse went on, yawning and rubbing its eyes, for it was getting very sleepy; ''and they drew all manner of things—everything that begins with an M—'' ''Why with an M?'' said Alice.', NULL);
INSERT OR IGNORE INTO alice_glossary (id, book_id, term, definition, example, chapter_reference)
