INSERT OR IGNORE INTO alice_glossary (id, book_id, term, definition, example, chapter_reference)
VALUES ('glossary-575', 'alice-in-wonderland', 'door', 'a swinging or sliding barrier that will close the entrance to a room or building or vehicle', 'There were doors all round the hall, but they were all locked; and when Alice had been all the way down one side and up the other, trying every door, she walked sadly down the middle, wondering how she was ever to get out again.', NULL);
