VALUES ('glossary-591', 'alice-in-wonderland', 'shoes', 'a particular situation', '''Oh, my poor little feet, I wonder who will put on your shoes and stockings for you now, dears?', NULL);
INSERT OR IGNORE INTO alice_glossary (id, book_id, term, definition, example, chapter_reference)
