VALUES ('glossary-1050', 'alice-in-wonderland', 'cheer', 'a cry or shout of approval', 'Then they all crowded round her once more, while the Dodo solemnly presented the thimble, saying ''We beg your acceptance of this elegant thimble''; and, when it had finished this short speech, they all cheered.', NULL);
INSERT OR IGNORE INTO alice_glossary (id, book_id, term, definition, example, chapter_reference)
VALUES ('glossary-1051', 'alice-in-wonderland', 'distraction', 'drawing someone''s attention away from something', '''Reeling and Writhing, of course, to begin with,'' the Mock Turtle replied; ''and then the different branches of Arithmetic—Ambition, Distraction, Uglification, and Derision.''', NULL);
INSERT OR IGNORE INTO alice_glossary (id, book_id, term, definition, example, chapter_reference)
VALUES ('glossary-1052', 'alice-in-wonderland', 'morsel', 'a small amount of solid food; a mouthful', 'Her chin was pressed so closely against her foot, that there was hardly room to open her mouth; but she did it at last, and managed to swallow a morsel of the lefthand bit.', NULL);
INSERT OR IGNORE INTO alice_glossary (id, book_id, term, definition, example, chapter_reference)
VALUES ('glossary-1053', 'alice-in-wonderland', 'grass', 'narrow-leaved green herbage: grown as lawns', 'Alice looked all round her at the flowers and the blades of grass, but she did not see anything that looked like the right thing to eat or drink under the circumstances.', NULL);
INSERT OR IGNORE INTO alice_glossary (id, book_id, term, definition, example, chapter_reference)
