INSERT OR IGNORE INTO alice_glossary (id, book_id, term, definition, example, chapter_reference)
VALUES ('glossary-1080', 'alice-in-wonderland', 'run on', 'continue uninterrupted', '''If I''d been the whiting,'' said Alice, whose thoughts were still running on the song, ''I''d have said to the porpoise, "Keep back, please: we don''t want YOU with us!"''', NULL);
