INSERT OR IGNORE INTO alice_glossary (id, book_id, term, definition, example, chapter_reference)
VALUES ('glossary-1105', 'alice-in-wonderland', 'lick', 'pass the tongue over', 'She is such a dear quiet thing,'' Alice went on, half to herself, as she swam lazily about in the pool, ''and she sits purring so nicely by the fire, licking her paws and washing her face—and she is such a nice soft thing to nurse—and she''s such a capital one for catching mice—oh, I beg your pardon!'' cried Alice again, for this time the Mouse was bristling all over, and she felt certain it must be really offended.', NULL);
INSERT OR IGNORE INTO alice_glossary (id, book_id, term, definition, example, chapter_reference)
VALUES ('glossary-1106', 'alice-in-wonderland', 'flavour', 'the taste experience when a savory condiment is taken into the mouth', 'However, this bottle was NOT marked ''poison,'' so Alice ventured to taste it, and finding it very nice, (it had, in fact, a sort of mixed flavour of cherry-tart, custard, pine-apple, roast turkey, toffee, and hot buttered toast,) she very soon finished it off.', NULL);
INSERT OR IGNORE INTO alice_glossary (id, book_id, term, definition, example, chapter_reference)
VALUES ('glossary-1107', 'alice-in-wonderland', 'last', 'coming after all others in time or space or degree or being the only one remaining', 'Indeed, she had quite a long argument with the Lory, who at last turned sulky, and would only say, ''I am older than you, and must know better''; and this Alice would not allow without knowing how old it was, and, as the Lory positively refused to tell its age, there was no more to be said.', NULL);
INSERT OR IGNORE INTO alice_glossary (id, book_id, term, definition, example, chapter_reference)
VALUES ('glossary-1108', 'alice-in-wonderland', 'ache', 'a dull persistent (usually moderately intense) pain', 'It quite makes my forehead ache!''', NULL);
INSERT OR IGNORE INTO alice_glossary (id, book_id, term, definition, example, chapter_reference)
