INSERT OR IGNORE INTO alice_glossary (id, book_id, term, definition, example, chapter_reference)
VALUES ('glossary-1110', 'alice-in-wonderland', 'pass on', 'place into the hands or custody of', 'The Queen smiled and passed on.', NULL);
INSERT OR IGNORE INTO alice_glossary (id, book_id, term, definition, example, chapter_reference)
