INSERT OR IGNORE INTO alice_glossary (id, book_id, term, definition, example, chapter_reference)
VALUES ('glossary-1797', 'alice-in-wonderland', 'run away', 'flee; take to one''s heels; cut and run', 'think they play at all fairly,'' Alice began, in rather a complaining tone, ''and they all quarrel so dreadfully one can''t hear oneself speak—and they don''t seem to have any rules in particular; at least, if there are, nobody attends to them—and you''ve no idea how confusing it is all the things being alive; for instance, there''s the arch I''ve got to go through next walking about at the other end of the ground—and I should have croqueted the Queen''s hedgehog just now, only it ran away when it saw', NULL);
INSERT OR IGNORE INTO alice_glossary (id, book_id, term, definition, example, chapter_reference)
VALUES ('glossary-1798', 'alice-in-wonderland', 'faintly', 'to a faint degree or weakly perceived', 'Alice panted as she ran; but the Gryphon only answered ''Come on!'' and ran the faster, while more and more faintly came, carried on the breeze that followed them, the melancholy words:— ''Soo—oop of the e—e—evening, Beautiful, beautiful Soup!''', NULL);
INSERT OR IGNORE INTO alice_glossary (id, book_id, term, definition, example, chapter_reference)
